import {useEffect, useState} from 'react';
import './Portfolio.scss';
import Loader from 'react-loaders';

const Portfolio = () => {
  const panels = document.querySelectorAll('.panel');
  const [panelAnimateClass, setPanelAnimateClass] = useState('panel-animate');
  function handleClick(e) {
    console.log(e.target);

    function toggleOpen() {
      this.classList.toggle('open');
    }

    function toggleActive(e) {
      if (e.propertyName.includes('flex')) {
        this.classList.toggle('open-active');
      }
    }

    panels.forEach((panel) => panel.addEventListener('click', toggleOpen));
    panels.forEach((panel) => panel.addEventListener('transitionend', toggleActive));

  }



    // function toggleOpen() {
    //   this.classList.toggle("open");
    // }

    // function toggleActive(e) {
    //   if (e.propertyName.includes("flex")) {
    //     this.classList.toggle("open-active");
    //   }
    // }


    // const panels = document.querySelectorAll(".panel");
    // panels.forEach((panel) => panel.addEventListener("click", toggleOpen));
    // panels.forEach((panel) =>
    //   panel.addEventListener("transitionend", toggleActive)
    // );

    return (
      <>
        <div className="container portfolio-page">
          <div className="portfolio-zone">
            <h1>Portfolio</h1>
            <section className="panels">
              <div onClick={handleClick} className="panel panel1">
                <p>Big</p>
                <p className="tech">
                  <a
                    href="https://github.com/mrdekraker/SQL-big-brother"
                    target="_blank"
                    rel="noreferrer">
                    SQL/Inquirer
                  </a>
                </p>
                <p>Brother</p>
              </div>
              <div onClick={handleClick} className="panel panel2">
                <p>Zookeeper</p>
                <p className="tech">
                  <a
                    href="https://github.com/mrdekraker/zookeeper"
                    target="_blank"
                    rel="noreferrer">
                    SQL/EXPRESS
                  </a>
                </p>
                <p>Animals</p>
              </div>
              <div onClick={handleClick} className="panel panel3">
                <p>Team</p>
                <p className="tech">
                  <a
                    href="https://github.com/mrdekraker/team-teamwork"
                    target="_blank"
                    rel="noreferrer">
                    Inquirer/Jest
                  </a>
                </p>
                <p>Teamwork</p>
              </div>
              <div onClick={handleClick} className="panel panel4">
                <p>Whatever</p>
                <p className="tech">
                  <a
                    href="https://github.com/mrdekraker/weather-app"
                    target="_blank"
                    rel="noreferrer">
                    API Manipulation
                  </a>
                </p>
                <p>Weather</p>
              </div>
              <div onClick={handleClick} className="panel panel5">
                <p>Round Table</p>
                <p className="tech">
                  <a
                    href="https://round-table14.herokuapp.com/"
                    target="_blank"
                    rel="noreferrer">
                    Socket.IO
                  </a>
                </p>
                <p>Chat App</p>
              </div>
            </section>
          </div>
        </div>
        <Loader type="line-scale-pulse-out" />
      </>

    );
  }

export default Portfolio;
