import './Portfolio.scss';
import Loader from 'react-loaders';

const Portfolio = () => {

  function PanelClickAnimate() {
    const [toggle, setToggle] = useState('panel-animate');





    // function toggleOpen() {
    //   this.classList.toggle("open");
    // }

    // function toggleActive(e) {
    //   if (e.propertyName.includes("flex")) {
    //     this.classList.toggle("open-active");
    //   }
    // }


    // const panels = document.querySelectorAll(".panel");
    // panels.forEach((panel) => panel.addEventListener("click", toggleOpen));
    // panels.forEach((panel) =>
    //   panel.addEventListener("transitionend", toggleActive)
    // );

    return (
      <>
        <div className="container portfolio-page">
          <div className="portfolio-zone">
            <h1>Portfolio</h1>
            <section onClick={() => setToggleClass(!toggleClass)} className="panels">
              <div class="panel panel1">
                <p>Big</p>
                <p class="tech">
                  <a
                    href="https://github.com/mrdekraker/SQL-big-brother"
                    target="_blank"
                    rel="noreferrer">
                    SQL/Inquirer
                  </a>
                </p>
                <p>Brother</p>
              </div>
              <div class="panel panel2">
                <p>Zookeeper</p>
                <p class="tech">
                  <a
                    href="https://github.com/mrdekraker/zookeeper"
                    target="_blank"
                    rel="noreferrer">
                    SQL/EXPRESS
                  </a>
                </p>
                <p>Animals</p>
              </div>
              <div class="panel panel3">
                <p>Team</p>
