                  <a
                    href="https://github.com/mrdekraker/weather-app"
                    target="_blank"
                    rel="noreferrer">
                    API Manipulation
                  </a>
                </p>
                <p>Weather</p>
              </div>
              <div class="panel panel5">
                <p>Round Table</p>
                <p class="tech">
                  <a
                    href="https://round-table14.herokuapp.com/"
                    target="_blank"
                    rel="noreferrer">
                    Socket.IO
                  </a>
                </p>
                <p>Chat App</p>
              </div>
            </section>
          </div>
        </div>
        <Loader type="line-scale-pulse-out" />
      </>

    );
  }
}

export default Portfolio;
