import './Portfolio.scss';
import Loader from 'react-loaders';
import ProjectCard from './ProjectCard/Card';

const Portfolio = () => {
  const projects = [
    {
      name: 'Big Brother',
      tech: 'SQL/Inquirer',
      description: 'A CLI application that allows the user to view, add, and update employees, roles, and departments in a company database.',
      imgUrl: '../../assets/images/big-brother.png'
    },
    {
      name: 'Zookeeper',
      tech: 'SQL/EXPRESS',
      description: 'A CLI application that allows the user to view, add, and update animals in a zoo database.',
      imgUrl: '../../assets/images/zookeeper.png'
    },
    {
      name: 'Team Teamwork',
      tech: 'Inquirer/Jest',
      description: 'A CLI application that allows the user to create a team of employees and generate an HTML page that displays the team roster.',
      imgUrl: '../../assets/images/team-teamwork.png'
    },
    {
      name: 'Whatever Weather',
      tech: 'API Manipulation',
      description: 'A web application that allows the user to search for a city and view the current weather and a 5-day forecast.',
      imgUrl: '../../assets/images/whatever-weather.png'
    },
    {
      name: 'Round Table',
      tech: 'mySQL/EXPRESS',
      description: 'A web application that allows the user to create a profile, search for and join other chat rooms, and chat between friends or colleagues.',
      imgUrl: '../../assets/images/round-table.png'
    },
  ];

  return (
      <>
        <div className="container portfolio-page">
          <div className="portfolio-zone">
            <h1>Portfolio</h1>
            <section className="panels">
              <ProjectCard />

            </section>
          </div>
        </div>
        <Loader type="line-scale-pulse-out" />
      </>

    );
  }

export default Portfolio;
